class Answered_Test(object):

	#This is the default constructor that assigns the id and opens the sections.
	def __init__(self, test_id):
		self.id = test_id
		self.sections = {}

	#This method adds a constructed section to the Answered_Test object.
	def add_section(self, section):
		assert(type(section) == Answered_Section)
		self.sections[section.index] = section

class Answered_Section(object):

	#This is the default constructor that assigns the index. Type not necessary.
	def __init__(self, index):
		self.index = index
		self.questions = {}

	#This method adds a constructed question to the Answered_Section object.
	def add_question(self, question):
		assert(type(question) == Answered_Question)
		self.questions[question.number] = question

	#This method returns the size of the section.
	def size(self):
		return len(self.questions)

class Answered_Question(object):

	#This is the default constructor that assigns the number and the answer.
	def __init__(self, index, answer):
		self.number = index
		self.answer = answer


